// C++ program for addition of two matrices
#include <bits/stdc++.h>
using namespace std;
#define n 3

// Function adds mat1[][] and mat2[][], and stores the result in mat3[][]
void add(int mat1[][n], int mat2[][n], int mat3[][n])
{
    int i, j;
    for(i=0; i<n; i++)
    {
        for(j=0; j<n; j++)
            mat3[i][j] = mat1[i][j]+mat2[i][j];
    }
}

