```c
#include <stdio.h> /*printf, scanf*/

int IsLowerCase(char character);

int main()
{
    char character = 0;

    int is_good = scanf("%c",&character);

    if(is_good != 1)
    {
        return -1;
    }

    if(IsLowerCase(character))
    {
        printf("lower case\n");
    }

    return 0;
}


int IsLowerCase(char character)
{
    if(character <= 'z' && character >= 'a')
    {
        return 1;
    }

    return 0;
}
```

```cpp
#include <iostream>
using namespace std;

void checkLowerCase(char ch){
    if(ch >= 'a' && ch <= 'z'){
        cout << "Lower Case";
    }
    else if(ch >= 'A' && ch <= 'Z'){
        cout << "Upper Case";
    }
    else{
        cout << "The character is not an alphabet";
    }
}

int main(){
    char ch;
    cout << "Enter a character: ";
    cin >> ch;
    checkLowerCase(ch);
    return 0;
}
```

