```ts
const checkPalindromeString = (text: string) => {
    text = text.toLowerCase();
    let n = text.length;
    let low = 0;
    let high = n - 1;

    while (high > low) {
        if (text[high] != text[low]) {
            return false;
        }
        low++;
        high--;
    }
    return true;
};

