bool IsPangram(string str)
{
    int arr[26] = {0};

    for(size_t i = 0; i < str.size(); ++i) {
        if((tolower(str[i]) >= 'a') && (tolower(str[i]) <= 'z')) {
            ++arr[tolower(str[i]) - 'a'];
        }
    }

    for(size_t i = 0; i < 26; ++i) {
        if(!arr[i]) {
            return false;
        }
    }

    return true;
}
