```cpp
#include <iostream>
#include <string>
#include <cctype>

using namespace std;

// Replaces all characters in string to the opposite case
string AlternateCase(string str) {
	int len = str.length();
	bool cap = false;
	for (int i = 0; i < len; i++) {
		if (cap == false) {
			str[i] = toupper(str[i]);
			cap = true;
		}
		else if (cap == true) {
			str[i] = tolower(str[i]);
			cap = false;
		}
	}
	return str;
}


int main()
{
	string str = "Hello World";
	str = AlternateCase(str);
	cout << str;
}
```

```cs
using System;

class MainClass {
    public static void Main (string[] args) {
    Console.WriteLine ("Please enter a String:");
    string str = Console.ReadLine();
    string x = "";
    for (int i = 0; i < str.Length; i++) {
      char ch = str[i];
      if (i%2 == 0) {
        x += Char.ToLower(ch);
      }
      else {
        x += Char.ToUpper(ch);
      }
    }
    Console.WriteLine("Alternating Case :"+x);
  }
}
