#include <iostream>

using namespace std;

int main()
{

    int size;
    cout << "\n\nEnter the size of the array -> ";
    cin >> size;

    int arr[size];

    cout << "\nEnter the elements of array -> \n";
    int temp = 0;

    while (temp < size)
    {
        cin >> arr[temp];
        temp++;
    }

    int largest = INT_MIN;

    for (int i = 0; i < temp; i++)
    {
        if (arr[i] > largest)
        {
            largest = arr[i];
        }
    }

    for (int i = 0; i < temp; i++)
    {
        if (arr[i] == largest)
        {
            arr[i] = INT_MIN;
        }
    }

    largest = INT_MIN;
    int tick = 0;

    for (int i = 0; i < temp; i++)
    {
        if (arr[i] > largest)
        {
            tick = 1;
            largest = arr[i];
        }
    }
    if (tick == 1)
    {
        cout << "\nThe second largest number in array is -> " << largest << endl
             << endl;
    }
    else
    {
        cout << "\nAll the elements are equal" << endl
             << endl;
    }

    return 0;
}
```

```java
public class FindSecondLargestNumberInAnArray{

    public static void main(String[] args) {

        int[] arr =  {5,4,3,2,1};

        System.out.println(returnSecondLargest(arr));

    }
    public static int returnSecondLargest(int[] arr) {
        for(int i=0; i<arr.length; i++){

            int minIndex = i;

            for(int j=i+1; j<arr.length; j++){
                if(arr[i] > arr[j]){
                    minIndex = j;
                }
            }

            int temp = arr[i];
            arr[i] = arr[minIndex];
            arr[minIndex] = temp;
        }

        return arr[arr.length-2];
    }
}
