```js
function findSecondLargestNumberInAnArray(arr) {
    let maxNum = Math.max(...arr)

    let indexOfMaxNum = arr.indexOf(maxNum)

    arr.splice(indexOfMaxNum, 1)

    return Math.max(...arr)
}
